  const submitHandler = event => {
     event.preventDefault();

    const enteredAmount = amountInputRef.current

  };

    return (
    <form className={classes.form} onSubmit={submitHandler}>

        <Input 
          ref={amountInputRef}
          label="Amount" 
          input={{
            id: 'amount' + props.id,
            type: 'number',
            min: '1',
            max: '9999999999',
